import React from "react";
import { Switch, Route, Link } from "react-router-dom";
import { ReactComponent as MTASvg } from './MTA_NYC_logo.svg';
//import { ReactComponent as MapSvg} from './New_York_Subway_Map_Alargule.svg';
import { ReactComponent as SubwaySvg } from './New_York_Subway_Map_Alargule.svg';

import "./Homepage.css";

import MenuItem from "@mui/material/MenuItem";
import Button from "@mui/material/Button";
import Menu from "@mui/material/Menu";

// import your component functions for use in route links
import SubwayLinesInfo from "./SubwayLinesInfo";
import SubwayStation from "./SubwayStation";
import SubwayStations from "./SubwayStations";
import Subwaylines from "./Subwaylines";

// url to a valid topojson file

//const geoUrl ="https://cdn.jsdelivr.net/npm/world-atlas@2/land-50m.json";

//const geoUrl = "https://raw.githubusercontent.com/deldersveld/topojson/master/countries/us-states/NY-36-new-york-counties.json";

//const geoUrl = "https://raw.githubusercontent.com/dwillis/nyc-maps/master/boroughs.geojson";

import {
  ComposableMap,
  Geographies,
  Geography,
} from "react-simple-maps"
//const geoUrl = "https://raw.githubusercontent.com/deldersveld/topojson/master/continents/north-america.json";
const geoUrl = "https://raw.githubusercontent.com/zcreativelabs/react-simple-maps/master/topojson-maps/world-110m.json";


function App() {
  // add links to your pages for now
  return (
    <Switch>
      <Route exact path="/" component={Homepage}></Route>
      <Route path="/subwayLinesInfo/:id" component={SubwayLinesInfo}></Route>
      <Route exact path="/stations" component={SubwayStations}></Route>
      <Route path="/stations/:id" component={SubwayStation}></Route>
      <Route exact path="/stationView" component={SubwayStation}></Route>
      <Route exact path="/lines" component={Subwaylines}></Route>
    </Switch>
  );
}

const Homepage = () => {
  const [anchorEl, setAnchorEl] = React.useState(null);

  const handleClose = () => {
    setAnchorEl(null);
  };

  const handleClick = (event) => {
    setAnchorEl(event.currentTarget);
  };

  return (
    <div>
      <table> <tr><td>
      <div>   
   <MTASvg style={{ height: 100, width: 100 }}/>
 </div></td><td>
       <div>
   <SubwaySvg style={{ height: 100, width: 100 }}/>
 </div></td></tr> </table>
    <div>
      <h2>Welcome to NYCRoute!</h2>
      <Button
        aria-controls="simple-menu"
        aria-haspopup="true"
        onClick={handleClick}
      >
        Start your route here!
      </Button>
      <Menu
        keepMounted
        anchorEl={anchorEl}
        onClose={handleClose}
        open={Boolean(anchorEl)}
      >
        <MenuItem onClick={handleClose}>Map View</MenuItem>
        <Link className="App-link" to="/stations">
          {" "}
          <MenuItem onClick={handleClose}>List of Stations</MenuItem>{" "}
        </Link>
        <Link className="App-link" to="/lines">
          {" "}
          <MenuItem onClick={handleClose}>List of Subway Lines</MenuItem>
        </Link>
      </Menu>
    </div>
     <div>
     <ComposableMap
 projectionConfig={{
   scale: 175,
   rotation: [0, 0, 0],
 }}
 width={800}
 height={400}
 style={{ width: "100%", height: "100%" }} 
>
       <Geographies geography={geoUrl}>
         {({geographies}) => geographies.map(geo =>
           <Geography key={geo.rsmKey} geography={geo} />
         )}
       </Geographies>
     </ComposableMap>
   </div>
   </div>
  );
};

export default App;
